const playerName = global.get('playerName')
const playerId = msg.payload.ids[0]
global.set('latestPlayerId', playerId)
global.set(playerId, 0)
return {
    ...msg,
    payload: {
        playerName,
        playerId,
    }
};

const playerId = msg.payload
const score = global.get(playerId)
return {
    ...msg,
    payload: {
        playerId,
        score,
    }
};

const result = msg.payload.result
const playerId = global.get('latestPlayerId')
const state = global.get('state')
if (!playerId || !state) {
    return {
        ...msg,
        payload: 'not found',
    };
}
let score = Number(global.get(playerId))
if (state === 'start' || state === 'wait') {
    if (result === 'win') {
        score += 1;
    } else if (result === 'victory') {
        score += 2;
    }
}
global.set(playerId, score)
return {
    ...msg,
    payload: {
        playerId,
        score,
    },
};

const name = msg.payload.name
global.set('playerName', name)
return {
    ...msg,
    payload: [
        {
            name : { value : name}
        }
    ]
};

const playerId = msg.payload.playerId
const state = msg.payload.state
global.set('state', state)
const score = Number(global.get(playerId))
return {
    ...msg,
    payload: [
        {
            id: playerId,
            record: {
                state : { value: state },
                score: { value: score },
            }
        }
    ]
};

const records = msg.payload.records;

if (records.length === 0) {
    return {
        ...msg,
        payload: records,
    };
}

const sortedRecords = records.map((record) => ({
    playerId: record.id.value,
    playerName: record.name.value,
    score: Number(record.score.value)
})).sort((a, b) => b.score - a.score);

let rank = 1;
let currentScore = sortedRecords[0].score;
sortedRecords[0].rank = rank;

for (let i = 1; i < sortedRecords.length; i++) {
    if (sortedRecords[i].score < currentScore) {
        rank = i + 1;
        currentScore = sortedRecords[i].score;
    }
    sortedRecords[i].rank = rank;
}

return {
    ...msg,
    payload: sortedRecords
};

const state = global.get('state') ?? 'end'
return {
    ...msg,
    payload: {
        state,
    }
};
